mapper.select:
SELECT attachfileno, contentsno, fname, fupname, thumb, fsize, rdate
FROM attachfile
WHERE attachfileno=#{attachfileno}

SELECT attachfileno, contentsno, fname, fupname, thumb, fsize, rdate
FROM attachfile
WHERE contentsno=#{contentsno}

SELECT COUNT(*) as cnt
FROM attachfile
WHERE contentsno=#{contentsno}
